import React, { useState } from 'react';

const ProductForm = ({ handleSubmit, error }) => {
  // Inicialize o estado 'product' com valores padrão
  const [product, setProduct] = useState({
    name: '',
    price: 0,
    stock_quantity: 0,
    description: '',
  });

  // Estado para controlar a visibilidade do formulário
  const [showForm, setShowForm] = useState(false);

  // Função para alternar a visibilidade do formulário
  const toggleFormVisibility = () => {
    setShowForm(!showForm);
  };

  // Função para tratar mudanças nos inputs
  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setProduct((prevProduct) => ({
      ...prevProduct,
      [name]: value,
    }));
  };

  // Função para enviar o formulário
  const onSubmit = (event) => {
    event.preventDefault();
    // Passa o produto para o handleSubmit
    handleSubmit(product);
  };

  return (
    <div>
      {/* Botão para mostrar/ocultar o formulário */}
      <button onClick={toggleFormVisibility}>
        {showForm ? 'Cancel' : 'Create New Product'}
      </button>

      {/* O formulário só será exibido se showForm for verdadeiro */}
      {showForm && (
        <form onSubmit={onSubmit}>
          <div>
            <label>Name</label>
            <input
              type="text"
              name="name"
              value={product.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div>
            <label>Price</label>
            <input
              type="number"
              name="price"
              value={product.price}
              onChange={handleInputChange}
              required
            />
          </div>
          <div>
            <label>Stock Quantity</label>
            <input
              type="number"
              name="stock_quantity"
              value={product.stock_quantity}
              onChange={handleInputChange}
              required
            />
          </div>
          <div>
            <label>Description</label>
            <textarea
              name="description"
              value={product.description}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit">Create Product</button>
        </form>
      )}

      {/* Exibir erro, caso haja algum */}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default ProductForm;
