import React, { useState } from 'react';

const ProductList = ({ products, onEditProduct, onBackToHome }) => {
  const [selectedProduct, setSelectedProduct] = useState(null); // Controla o produto selecionado

  // Função para lidar com o clique no produto
  const handleProductClick = (product) => {
    // Exibe mais informações do produto clicado
    setSelectedProduct(selectedProduct && selectedProduct.id === product.id ? null : product);
  };

  return (
    <div>
      {/* Link para voltar para a home */}
      <button onClick={onBackToHome}>Back to Home</button>

      <ul>
        {products.length > 0 ? (
          products.map((product) => (
            <li key={product.id}>
              <button
                type="button"
                onClick={() => handleProductClick(product)} // Exibe detalhes ao clicar no nome do produto
                style={{ textDecoration: 'underline', cursor: 'pointer', background: 'none', border: 'none', color: 'blue' }}
              >
                {product.name}
              </button>
              {/* Exibe mais detalhes do produto ao clicar */}
              {selectedProduct && selectedProduct.id === product.id && (
                <div>
                  <p><strong>Name:</strong> {product.name}</p>
                  <p><strong>Price:</strong> {product.price.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}</p>
                  <p><strong>Stock Quantity:</strong> {product.stock_quantity}</p>
                  <p><strong>Description:</strong> {product.description}</p>
                  <button onClick={() => onEditProduct(product.id)}>Edit</button> {/* Editar o produto */}
                </div>
              )}
            </li>
          ))
        ) : (
          <p>No products available.</p>
        )}
      </ul>
    </div>
  );
};

export default ProductList;
