import React, { useState } from 'react';

const ProductForm = ({ handleSubmit, handleInputChange, product, error }) => {
  // Estado para controlar a visibilidade do formulário
  const [showForm, setShowForm] = useState(false);

  // Função para alternar a visibilidade do formulário
  const toggleFormVisibility = () => {
    setShowForm(!showForm);
  };

  return (
    <div>
      {/* Botão para mostrar/ocultar o formulário */}
      <button onClick={toggleFormVisibility}>
        {showForm ? 'Cancel' : 'Create New Product'}
      </button>

      {/* O formulário só será exibido se showForm for verdadeiro */}
      {showForm && (
        <form onSubmit={handleSubmit}>
          <div>
            <label>Name</label>
            <input
              type="text"
              name="name"
              value={product.name}
              onChange={handleInputChange}
              required
            />
          </div>
          <div>
            <label>Price</label>
            <input
              type="number"
              name="price"
              value={product.price}
              onChange={handleInputChange}
              required
            />
          </div>
          <div>
            <label>Stock Quantity</label>
            <input
              type="number"
              name="stock_quantity"
              value={product.stock_quantity}
              onChange={handleInputChange}
              required
            />
          </div>
          <div>
            <label>Description</label>
            <textarea
              type="text"
              name="description"
              value={product.description}
              onChange={handleInputChange}
              required
            />
          </div>
          <button type="submit">Create Product</button>
        </form>
      )}

      {/* Exibir erro, caso haja algum */}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
};

export default ProductForm;
