import React, { useState, useEffect } from 'react';
import ProductList from './components/ProductList';
import ProductForm from './components/ProductForm';
import { getProducts, updateProduct, deleteProduct } from './api'; // Corrigido para incluir deleteProduct

function App() {
  const [products, setProducts] = useState([]); // Estado para armazenar os produtos
  const [isFormVisible, setIsFormVisible] = useState(false); // Controla a visibilidade do formulário
  const [isListVisible, setIsListVisible] = useState(false); // Controla a visibilidade da lista de produtos
  const [selectedProduct, setSelectedProduct] = useState(null); // Produto selecionado para exibição

  // Função para buscar os produtos da API
  const fetchProducts = async () => {
    try {
      const response = await getProducts(); // Chama a API para pegar a lista de produtos
      setProducts(response); // Atualiza o estado com a nova lista de produtos
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  // Função chamada quando um novo produto é salvo ou editado
  const handleProductSaved = async () => {
    await fetchProducts(); // Recarrega a lista de produtos após a criação ou atualização
    setIsFormVisible(false); // Fecha o formulário após salvar
    setSelectedProduct(null); // Limpa o produto selecionado
    setIsListVisible(true); // Volta para a lista de produtos
  };

  // Função para editar um produto
  const handleEditProduct = (productId) => {
    const product = products.find(p => p.id === productId); // Encontra o produto a ser editado
    setSelectedProduct(product); // Define o produto a ser editado
    setIsFormVisible(true); // Exibe o formulário de edição
    setIsListVisible(false); // Oculta a lista de produtos
  };

  // Função para excluir um produto
  const handleDeleteProduct = async (productId) => {
    try {
      await deleteProduct(productId); // Deleta o produto usando a API
      fetchProducts(); // Atualiza a lista de produtos após a exclusão
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  // Função para voltar para a lista de produtos
  const handleBackToList = () => {
    setIsListVisible(true);
    setIsFormVisible(false);
    setSelectedProduct(null);
  };

  useEffect(() => {
    fetchProducts(); // Carrega a lista de produtos ao montar o componente
  }, []); // O array vazio [] garante que a requisição será feita apenas uma vez

  return (
    <div>
      <h1>Omens</h1>

      {/* Botões "Create" e "List" */}
      {!isListVisible && !isFormVisible && (
        <div>
          <button onClick={() => setIsFormVisible(true)}>Create a new product</button>
          <button onClick={() => setIsListVisible(true)}>List of products</button>
        </div>
      )}

      {/* Formulário de criação ou edição */}
      {isFormVisible && (
        <ProductForm
          product={selectedProduct} // Passa o produto a ser editado para o formulário
          onProductSaved={handleProductSaved} // Passa a função de salvar
          onCancel={handleBackToList} // Volta para a lista de produtos
        />
      )}

      {/* Lista de produtos */}
      {isListVisible && !isFormVisible && (
        <ProductList
          products={products}
          onProductClick={(product) => setSelectedProduct(product)} // Exibe as informações do produto ao clicar
        />
      )}

      {/* Exibe as informações do produto selecionado */}
      {selectedProduct && !isFormVisible && (
        <div>
          <h2>Product Details</h2>
          <p><strong>Name:</strong> {selectedProduct.name}</p>
          <p><strong>Price:</strong> {selectedProduct.price.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}</p>
          <p><strong>Stock Quantity:</strong> {selectedProduct.stock_quantity}</p>
          <p><strong>Description:</strong> {selectedProduct.description}</p>

          {/* Botões de Editar e Deletar */}
          <button onClick={() => handleEditProduct(selectedProduct.id)}>Edit</button>
          <button onClick={() => handleDeleteProduct(selectedProduct.id)}>Delete</button>
        </div>
      )}
    </div>
  );
}

export default App;
