// src/components/ProductList.js
import React, { useState, useEffect } from 'react';
import { getProducts } from '../api';

function ProductList({ onEditProduct }) {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fazendo a requisição para a API
    const fetchProducts = async () => {
      try {
        const response = await getProducts();
        setProducts(response);  // Atualiza o estado com os produtos
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []); // O array vazio [] garante que a requisição seja feita apenas uma vez

  return (
    <div>
      <h2>Product List</h2>
      <ul>
        {products.map(product => (
          <li key={product.id}>
            <p>{product.name} - ${product.price}</p>
            <button onClick={() => onEditProduct(product.id)}>Edit</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default ProductList;
