import React, { useState, useEffect } from 'react';
import ProductList from './components/ProductList';
import ProductForm from './components/ProductForm';
import { getProducts, updateProduct, deleteProduct } from './api';

function App() {
  const [products, setProducts] = useState([]);
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [isListVisible, setIsListVisible] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  // Função para buscar os produtos
  const fetchProducts = async () => {
    try {
      const response = await getProducts();
      setProducts(response);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  // Função chamada quando um novo produto é salvo ou editado
  const handleProductSaved = async () => {
    await fetchProducts();
    setIsFormVisible(false);
    setSelectedProduct(null);
    setIsListVisible(true); // Retorna à lista após salvar ou editar
  };

  // Função para editar um produto
  const handleEditProduct = (productId) => {
    const product = products.find(p => p.id === productId);
    setSelectedProduct(product);
    setIsFormVisible(true);
    setIsListVisible(false); // Oculta a lista ao editar
  };

  // Função para excluir um produto
  const handleDeleteProduct = async (productId) => {
    try {
      await deleteProduct(productId);
      fetchProducts(); // Recarrega os produtos após a exclusão
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  // Função para voltar à lista de produtos ou à tela inicial
  const handleBackToHome = () => {
    setIsListVisible(true);
    setIsFormVisible(false);
    setSelectedProduct(null); // Limpa a seleção do produto
  };

  useEffect(() => {
    fetchProducts(); // Carrega os produtos ao inicializar o componente
  }, []);

  return (
    <div>
      <h1>Omens</h1>

      {/* Botões "Create" e "List" */}
      {!isListVisible && !isFormVisible && (
        <div>
          <button onClick={() => setIsFormVisible(true)}>Create a new product</button>
          <button onClick={() => setIsListVisible(true)}>List of products</button>
        </div>
      )}

      {/* Formulário de criação ou edição */}
      {isFormVisible && (
        <ProductForm
          product={selectedProduct}
          onProductSaved={handleProductSaved}
          onCancel={handleBackToHome}
        />
      )}

      {/* Lista de produtos */}
      {isListVisible && !isFormVisible && (
        <ProductList
          products={products}
          onProductClick={(product) => setSelectedProduct(product)}
          onBackToHome={handleBackToHome} // Passando a função de voltar para a tela inicial
        />
      )}

      {/* Exibe as informações do produto selecionado */}
      {selectedProduct && !isFormVisible && (
        <div>
          <h2>Product Details</h2>
          <p><strong>Name:</strong> {selectedProduct.name}</p>
          <p><strong>Price:</strong> {selectedProduct.price.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}</p>
          <p><strong>Stock Quantity:</strong> {selectedProduct.stock_quantity}</p>
          <p><strong>Description:</strong> {selectedProduct.description}</p>

          {/* Botões de Editar e Deletar */}
          <button onClick={() => handleEditProduct(selectedProduct.id)}>Edit</button>
          <button onClick={() => handleDeleteProduct(selectedProduct.id)}>Delete</button>

          {/* Botão de Voltar para a Tela Inicial */}
          <button onClick={handleBackToHome}>Voltar</button>
        </div>
      )}
    </div>
  );
}

export default App;
