// src/components/ProductForm.js
import React, { useState } from 'react';
import { createProduct } from '../api'; // Assumindo que você tenha uma função para interagir com a API

function ProductForm({ onProductCreated }) {
  const [product, setProduct] = useState({
    name: '',
    price: '',
    stock_quantity: '',
    description: '',
  });

  const [error, setError] = useState(null);

  // Função para lidar com mudanças nos inputs
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProduct({
      ...product,
      [name]: value,
    });
  };

  // Função para enviar o formulário
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Envia os dados para o backend Rails via API
      await createProduct(product);

      // Chama a função para atualizar a lista de produtos no pai (caso tenha um)
      onProductCreated();

      // Limpa o formulário
      setProduct({
        name: '',
        price: '',
        stock_quantity: '',
        description: '',
      });
    } catch (error) {
      setError('Error creating product');
    }
  };

  return (
    <div>
      <h2>Create a New Product</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name</label>
          <input
            type="text"
            name="name"
            value={product.name}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Price</label>
          <input
            type="number"
            name="price"
            value={product.price}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Stock Quantity</label>
          <input
            type="number"
            name="stock_quantity"
            value={product.stock_quantity}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Description</label>
          <textarea
            name="description"
            value={product.description}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit">Create Product</button>
      </form>

      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default ProductForm;
