import React, { useEffect, useState } from "react";

export function Products() {
  const [content, setContent] = useState(<ProductList showForm={showForm} />);

  function showList() {
      setContent(<ProductList showForm={showForm} />);
  }

  function showForm() {
    setContent(<ProductForm showList={showList} />);
  }

  return (
    <div className="container my-5">
      {content}
    </div>

  );
}


function ProductList(props) {
  const[products, setProducts] = useState([]);

function fetchProducts() {
    fetch("http://localhost:3000/api/products")
    .then((response) => {
      if(!response.ok) {
        throw new Error("Unexpected Server Response");
      }
      return response.json()
    })
    .then((data) => {
      //console.log(data);
      setProducts(data);
    })

    .catch((error) => console.log("Error: ", error));
}

//fetchProducts();
useEffect(() => fetchProducts(), []);

  return(
    <>
    <h2 className="text-center mb-3">List of Products</h2>
    <button onClick={() => props.showForm()} type="button" className="btn btn-primary me-2">Create</button>
    <button onClick={() => fetchProducts()} type="button" className="btn btn-outline-primary me-2">Refresh</button>
    <table className="table">
      <thead>
          <tr>
              <th>id</th>
              <th>Nome</th>
              <th>Price</th>
              <th>Stock_quantity</th>
              <th>Description</th>
          </tr>
      </thead>
      <tbody>
        {
            products.map((product, index) => {
              return(
                <tr key={index}>

                    <td>{product.id}</td>
                    <td>{product.name}</td>
                    <td>{product.price}</td>
                    <td>{product.stock_quantity}</td>
                    <td>{product.description}</td>
                    <td style={{width: "10px", whiteSpace: "nowrap"}}>
                      <button type="button" className="btn btn-primary btn-5m me-2">Edit</button>
                      <button type="button" className="btn btn-danger btn-sm">Delete</button>
                    </td>

                </tr>
              );
            })
        }
      </tbody>
    </table>
    </>
  );
}

function ProductForm(props) {
  const [errorMessage, setErrorMessage] = useState("");

    function handleSubmit(event) {
        event.preventDefault();

        //read form data
        const formData = new FormData(event.target);

        //
        const product = Object.fromEntries(formData.entries());

        // form validation
        if(!product.name || !product.price || !product.stock_quantity || !product.description) {
          console.log("Please provide all the required fields!");
          setErrorMessage(
            <div className="alert alert-warning" role="alert">
              Please provide all the required fields!
            </div>
          )
          return;
        }

        // create a new product
        product.createdAT = new Date().toISOString().slice(0, 10);
        fetch("http://localhost:3000/api/products", {
            method: "POST",
            headers: {
              "Content-Type": "application/json"
            },
            body: JSON.stringify(product)
        })
        .then((response) => {
          if (!response.ok) {
              throw new Error("Network response was not OK")
          }
          return response.json()
        })
        .then((data) => props.showList())
        .catch((error) => {
          console.error("Error:", error);
        })

    }
  return(
    <>
    <h2 className="text-center mb-3">Create New Product</h2>

    <div className="row">
      <div className="col-lg-6 mx-auto">

        <form onSubmit={(event) => handleSubmit(event)}>
          <div className="row mb-3">
              <label className="col-sm-4 col-form-label">Name</label>
              <div className="col-sm-8">
                  <input className="form-control"
                      name="name"
                      defaultValue=""/>
              </div>
          </div>

          <div className="row mb-3">
              <label className="col-sm-4 col-form-label">Price</label>
              <div className="col-sm-8">
                  <input className="form-control"
                      name="price"
                      defaultValue=""/>
              </div>
          </div>


          <div className="row mb-3">
              <label className="col-sm-4 col-form-label">Stock_quantity</label>
              <div className="col-sm-8">
                  <input className="form-control"
                      name="stock_quantity"
                      defaultValue=""/>
              </div>
          </div>


          <div className="row mb-3">
              <label className="col-sm-4 col-form-label">Description</label>
              <div className="col-sm-8">
                  <textarea className="form-control"
                      name="description"
                      defaultValue=""/>
              </div>
          </div>


          <div className="row">
              <div className="offset-sm-4 col-sm-4 d-grid">
                <button type="submit" className="btn btn-primary btn-sm me-3">Save</button>
              </div>
              <div className="col-sm-4 d-grid">
                <button onClick={() => props.showList()} type="button" className="btn btn-secondary me-2">Cancel</button>
              </div>
          </div>
        </form>
      </div>
    </div>

    </>
  );
}
