// src/components/ProductForm.js
import React, { useState, useEffect } from 'react';
import { createProduct, updateProduct } from '../api';

const ProductForm = ({ productId, onProductSaved, onCancel }) => {
  const [product, setProduct] = useState({
    name: '',
    price: '',
    stock_quantity: '',
    description: ''
  });

  // Efeito para carregar o produto quando estamos em modo de edição (se productId for passado)
  useEffect(() => {
    if (productId) {
      // Aqui você poderia buscar os dados do produto para edição
      // Exemplo fictício de chamada para buscar o produto:
      // fetchProduct(productId).then(data => setProduct(data));
    }
  }, [productId]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setProduct(prevProduct => ({
      ...prevProduct,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (productId) {
        await updateProduct(productId, product);  // Atualiza o produto
      } else {
        await createProduct(product);  // Cria um novo produto
      }
      console.log("Product saved, calling onProductSaved...");
      onProductSaved();  // Chama a função para atualizar a lista de produtos
    } catch (error) {
      console.error("Error saving product:", error);
    }
  };

  return (
    <div>
      <h2>{productId ? 'Edit Product' : 'Create Product'}</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          value={product.name}
          onChange={handleChange}
          placeholder="Product Name"
        />
        <input
          type="number"
          name="price"
          value={product.price}
          onChange={handleChange}
          placeholder="Product Price"
        />
        <input
          type="number"
          name="stock_quantity"
          value={product.stock_quantity}
          onChange={handleChange}
          placeholder="Stock Quantity"
        />
        <input
          name="description"
          value={product.description}
          onChange={handleChange}
          placeholder="Product Description"
        />

        {/* Botões de Submit e Cancel */}
        <div style={{ display: 'flex', gap: '10px', marginTop: '10px' }}>
          <button type="submit">{productId ? 'Update' : 'Create'}</button>

          {/* Botão de Cancelar */}
          <button
            type="button"
            onClick={onCancel}
            style={{ backgroundColor: 'gray', color: 'white' }}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
};

export default ProductForm;
