// src/App.js
import React, { useState, useEffect } from 'react';
import ProductList from './components/ProductList';
import ProductForm from './components/ProductForm';
import { getProducts } from './api';

function App() {
  const [products, setProducts] = useState([]);
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [isEditVisible, setIsEditVisible] = useState(false);
  const [productToEdit, setProductToEdit] = useState(null);
  const [isListVisible, setIsListVisible] = useState(false);

  // Função para buscar os produtos da API
  const fetchProducts = async () => {
    try {
      const response = await getProducts(); // Chama a API para pegar a lista de produtos
      setProducts(response); // Atualiza a lista de produtos
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  // Função chamada quando um novo produto é salvo
  const handleProductCreated = () => {
    fetchProducts(); // Atualiza a lista de produtos após a criação
    setIsFormVisible(false); // Esconde o formulário após salvar
  };

  // Função chamada quando um produto é selecionado para edição
  const handleEditProduct = (product) => {
    setProductToEdit(product); // Define o produto a ser editado
    setIsEditVisible(true); // Exibe o formulário de edição
    setIsFormVisible(false); // Esconde o formulário de criação
  };

  useEffect(() => {
    fetchProducts(); // Carrega os produtos quando o componente é montado
  }, []);

  return (
    <div>
      <h1>Omens - Product Management</h1>

      {/* Exibir botão de criar produto apenas se nenhum formulário estiver visível */}
      {!isFormVisible && !isEditVisible && !isListVisible && (
        <div>
          <button onClick={() => setIsFormVisible(true)}>Create a new product</button>
        </div>
      )}

      {/* Botão "Back to Home" - Aparece após exibir o formulário ou lista */}
      {(isFormVisible || isEditVisible || isListVisible) && (
        <button
          onClick={() => {
            setIsFormVisible(false); // Esconde o formulário de criação
            setIsEditVisible(false); // Esconde o formulário de edição
            setIsListVisible(false); // Esconde a lista de produtos
          }}
          style={{ marginTop: '10px' }}
        >
          Back to Home
        </button>
      )}

      {/* Formulário de criação de produto */}
      {isFormVisible && !isEditVisible && (
        <ProductForm onProductSaved={handleProductCreated} onCancel={() => setIsFormVisible(false)} />
      )}

      {/* Formulário de edição de produto */}
      {isEditVisible && productToEdit && (
        <ProductForm
          productId={productToEdit.id}
          initialProduct={productToEdit}
          onProductSaved={handleProductCreated}
          onCancel={() => setIsEditVisible(false)}
        />
      )}

      {/* Lista de produtos */}
      {isListVisible && !isEditVisible && (
        <ProductList products={products} onEditProduct={handleEditProduct} />
      )}
    </div>
  );
}

export default App;
