// src/App.js
import React, { useState, useEffect } from 'react';
import ProductList from './components/ProductList';
import ProductForm from './components/ProductForm';
import { getProducts } from './api';

function App() {
  const [products, setProducts] = useState([]);  // Estado para armazenar os produtos
  const [isFormVisible, setIsFormVisible] = useState(false); // Estado para controlar a visibilidade do formulário

  // Função para buscar os produtos da API
  const fetchProducts = async () => {
    try {
      const response = await getProducts();  // Chama a API para pegar a lista de produtos
      setProducts(response);  // Atualiza o estado com a nova lista de produtos
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  // Função chamada quando um novo produto é salvo
  const handleProductSaved = () => {
    console.log('Product saved, fetching updated product list...');
    fetchProducts(); // Recarrega a lista de produtos após a criação ou atualização
    setIsFormVisible(false); // Fecha o formulário após salvar
  };

  useEffect(() => {
    fetchProducts();  // Carrega a lista de produtos ao montar o componente
  }, []);  // O array vazio [] garante que a requisição será feita apenas uma vez

  return (
    <div>
      <h1>Omens</h1>

      {/* Link "Create a new product", aparece quando o formulário não está visível */}
      {!isFormVisible && (
        <button
          onClick={(e) => {
            e.preventDefault(); // Impede qualquer navegação desnecessária
            setIsFormVisible(true); // Exibe o formulário
          }}
        >
          Create a new product
        </button>
      )}

      {/* O formulário só será exibido se isFormVisible for verdadeiro */}
      {isFormVisible && (
        <ProductForm onProductSaved={handleProductSaved} onCancel={() => setIsFormVisible(false)} />
      )}

      {/* Lista de produtos */}
      <ProductList products={products} />
    </div>
  );
}

export default App;
