import React, { useState, useEffect } from 'react';
import ProductList from './components/ProductList';
import ParentComponent from './components/ParentComponent'; // Importe o ParentComponent
import { getProducts } from './api';

function App() {
  const [products, setProducts] = useState([]);

  // Função para atualizar a lista de produtos
  const fetchProducts = async () => {
    const response = await getProducts();
    setProducts(response);
  };

  // Função chamada quando um novo produto é criado
  const handleProductCreated = () => {
    fetchProducts(); // Atualiza a lista de produtos
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div>
      <h1>Omens</h1>

      {/* Componente que exibe o link para criar um novo produto */}
      <ParentComponent onProductCreated={handleProductCreated} />

      {/* Lista de produtos */}
      <ProductList products={products} />
    </div>
  );
}

export default App;
