// src/App.js
import React, { useState, useEffect } from 'react';
import ProductList from './components/ProductList';
import ProductForm from './components/ProductForm';
import EditProductList from './components/EditProductList';
import { getProducts } from './api';

function App() {
  const [products, setProducts] = useState([]); // Estado para armazenar os produtos
  const [isFormVisible, setIsFormVisible] = useState(false); // Estado para controlar a visibilidade do formulário de criação
  const [isListVisible, setIsListVisible] = useState(false); // Estado para controlar a visibilidade da lista de produtos
  const [isEditListVisible, setIsEditListVisible] = useState(false); // Estado para controlar a visibilidade da lista de edição
  const [isEditVisible, setIsEditVisible] = useState(false); // Estado para controlar a visibilidade do formulário de edição
  const [productToEdit, setProductToEdit] = useState(null); // Estado para armazenar o produto a ser editado

  // Função para buscar os produtos da API
  const fetchProducts = async () => {
    try {
      const response = await getProducts(); // Chama a API para pegar a lista de produtos
      setProducts(response); // Atualiza o estado com a nova lista de produtos
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  // Função chamada quando um novo produto é salvo ou editado
  const handleProductSaved = () => {
    console.log('Product saved, fetching updated product list...');
    fetchProducts(); // Recarrega a lista de produtos após a criação ou atualização
    setIsFormVisible(false); // Fecha o formulário após salvar
    setIsEditVisible(false); // Fecha o formulário de edição
  };

  // Função para iniciar a edição de um produto
  const handleEditProduct = (productId) => {
    const product = products.find(p => p.id === productId); // Busca o produto que será editado
    setProductToEdit(product); // Define o produto a ser editado
    setIsEditVisible(true); // Exibe o formulário de edição
    setIsEditListVisible(false); // Oculta a lista de edição após selecionar um produto
  };

  useEffect(() => {
    fetchProducts(); // Carrega a lista de produtos ao montar o componente
  }, []); // O array vazio [] garante que a requisição será feita apenas uma vez

  return (
    <div>
      <h1>Omens</h1>

      {/* Link "Create a new product", aparece quando o formulário não está visível */}
      {!isFormVisible && !isListVisible && !isEditListVisible && !isEditVisible && (
        <>
          <button
            onClick={(e) => {
              e.preventDefault(); // Impede qualquer navegação desnecessária
              setIsFormVisible(true); // Exibe o formulário de criação
            }}
          >
            Create a new product
          </button>

          <button
            onClick={(e) => {
              e.preventDefault();
              setIsListVisible(true);  // Exibe a lista de produtos
            }}
          >
            List of products
          </button>

          <button
            onClick={(e) => {
              e.preventDefault();
              setIsEditListVisible(true);  // Exibe a lista de produtos para edição
            }}
          >
            Edit a product
          </button>
        </>
      )}

      {/* Link "Back to Home" - Aparece na lista de produtos ou no formulário de edição */}
      {(isListVisible || isEditVisible || isEditListVisible) && (
        <button
          onClick={() => {
            setIsListVisible(false); // Oculta a lista de produtos
            setIsEditListVisible(false); // Oculta a lista de edição
            setIsEditVisible(false); // Oculta o formulário de edição
          }}
          style={{ marginTop: '10px' }}
        >
          Back to Home
        </button>
      )}

      {/* Formulário de criação de produto */}
      {isFormVisible && (
        <ProductForm onProductSaved={handleProductSaved} onCancel={() => setIsFormVisible(false)} />
      )}

      {/* Lista de produtos */}
      {isListVisible && !isEditVisible && (
        <div>
          <h2>List of Products</h2>
          <ProductList products={products} />
        </div>
      )}

      {/* Lista de produtos para edição */}
      {isEditListVisible && !isEditVisible && (
        <div>
          <h2>Select a product to edit</h2>
          <EditProductList products={products} onSelectProduct={handleEditProduct} />
        </div>
      )}

      {/* Formulário de edição de produto */}
      {isEditVisible && productToEdit && (
        <div>
          <h2>Edit Product</h2>
          <ProductForm
            productId={productToEdit.id}
            initialProduct={productToEdit}
            onProductSaved={handleProductSaved}
            onCancel={() => {
              setIsEditVisible(false); // Fecha o formulário de edição
              setIsEditListVisible(true);  // Volta para a lista de produtos para edição
            }}
          />
        </div>
      )}
    </div>
  );
}

export default App;
