import React, { useState, useEffect } from 'react';
import ProductList from './components/ProductList';
import ProductForm from './components/ProductForm';
import { getProducts } from './api';

function App() {
  const [products, setProducts] = useState([]);

  // Função para buscar os produtos atualizados
  const fetchProducts = async () => {
    try {
      const response = await getProducts();  // Chama a API para pegar a lista de produtos
      setProducts(response);  // Atualiza o estado com a nova lista de produtos
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  // Função chamada quando um novo produto for criado
  const handleProductSaved = () => {
    fetchProducts(); // Atualiza a lista de produtos após a criação
  };

  useEffect(() => {
    fetchProducts();  // Carrega a lista de produtos ao montar o componente
  }, []);  // O array vazio [] garante que a requisição será feita apenas uma vez

  return (
    <div>
      <h1>Omens</h1>
      <ProductForm onProductSaved={handleProductSaved} /> {/* Passa a função de atualização de lista */}
      <ProductList products={products} /> {/* Exibe a lista de produtos */}
    </div>
  );
}

export default App;
