import React, { useState, useEffect } from 'react';
import { createProduct, updateProduct } from '../api'; // Funções para criar e atualizar produto

const ProductForm = ({ product, onProductSaved, onCancel }) => {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [stockQuantity, setStockQuantity] = useState('');
  const [description, setDescription] = useState('');

  // Popula os campos com dados do produto ao editar
  useEffect(() => {
    if (product) {
      setName(product.name);
      setPrice(product.price);
      setStockQuantity(product.stock_quantity);
      setDescription(product.description);
    }
  }, [product]);

  // Função para salvar o produto
  const handleSubmit = async (e) => {
    e.preventDefault();

    const productData = {
      name,
      price,
      stock_quantity: stockQuantity,
      description,
    };

    try {
      if (product) {
        // Se um produto for passado, estamos atualizando
        await updateProduct(product.id, productData);
      } else {
        // Se não houver produto, estamos criando um novo
        await createProduct(productData);
      }
      onProductSaved(); // Chama a função para recarregar a lista de produtos
    } catch (error) {
      console.error('Error saving product:', error);
    }
  };

  return (
    <div>
      <h2>{product ? 'Edit Product' : 'Create Product'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name</label>
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Price</label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Stock Quantity</label>
          <input
            type="number"
            value={stockQuantity}
            onChange={(e) => setStockQuantity(e.target.value)}
            required
          />
        </div>
        <div>
          <label>Description</label>
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </div>
        <button type="submit">{product ? 'Save Changes' : 'Create Product'}</button>
        <button type="button" onClick={onCancel}>Cancel</button>
      </form>
    </div>
  );
};

export default ProductForm;
