// src/App.js
import React, { useState, useEffect } from 'react';
import ProductList from './components/ProductList';
import ProductForm from './components/ProductForm';
import { getProducts } from './api';

function App() {
  const [products, setProducts] = useState([]);
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [isListVisible, setIsListVisible] = useState(false);
  const [isProductDetailsVisible, setIsProductDetailsVisible] = useState(false);
  const [productToView, setProductToView] = useState(null);

  // Função para buscar os produtos da API
  const fetchProducts = async () => {
    try {
      const response = await getProducts(); // Chama a API para pegar a lista de produtos
      setProducts(response); // Atualiza a lista de produtos
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  // Função chamada quando um novo produto é salvo
  const handleProductCreated = () => {
    fetchProducts(); // Atualiza a lista de produtos após a criação
    setIsFormVisible(false); // Esconde o formulário após salvar
  };

  // Função chamada para exibir os detalhes de um produto
  const handleViewProductDetails = (product) => {
    setProductToView(product);
    setIsProductDetailsVisible(true);
    setIsListVisible(false);
  };

  // Função chamada para voltar à página inicial
  const handleBackToHome = () => {
    setIsFormVisible(false);
    setIsListVisible(false);
    setIsProductDetailsVisible(false);
  };

  useEffect(() => {
    fetchProducts(); // Carrega os produtos quando o componente é montado
  }, []);

  return (
    <div>
      <h1>Omens - Product Management</h1>

      {/* Exibir botão de criar produto apenas se nenhum formulário estiver visível */}
      {!isFormVisible && !isListVisible && !isProductDetailsVisible && (
        <div>
          <button onClick={() => setIsFormVisible(true)}>Create a new product</button>
          <button onClick={() => setIsListVisible(true)}>List of Products</button>
        </div>
      )}

      {/* Botão "Back to Home" - Aparece após exibir o formulário ou lista */}
      {(isFormVisible || isListVisible || isProductDetailsVisible) && (
        <button onClick={handleBackToHome} style={{ marginTop: '10px' }}>
          Back to Home
        </button>
      )}

      {/* Formulário de criação de produto */}
      {isFormVisible && (
        <ProductForm onProductSaved={handleProductCreated} onCancel={() => setIsFormVisible(false)} />
      )}

      {/* Lista de produtos */}
      {isListVisible && !isProductDetailsVisible && (
        <ProductList products={products} onViewProduct={handleViewProductDetails} />
      )}

      {/* Detalhes do produto */}
      {isProductDetailsVisible && productToView && (
        <div>
          <h2>Product Details</h2>
          <p><strong>Name:</strong> {productToView.name}</p>
          <p><strong>Price:</strong> {productToView.price.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}</p>
          <p><strong>Stock Quantity:</strong> {productToView.stock_quantity}</p>
          <p><strong>Description:</strong> {productToView.description}</p>
        </div>
      )}
    </div>
  );
}

export default App;
