import React, { useState } from 'react';

const ProductList = ({ products, onEditProduct, onBackToHome }) => {
  const [selectedProduct, setSelectedProduct] = useState(null); // Controla o produto selecionado
  const [isEditingProductListVisible, setIsEditingProductListVisible] = useState(false); // Controla a visibilidade da lista de edição de produtos

  // Função para lidar com o clique no produto e alternar entre mostrar detalhes ou não
  const handleProductClick = (product) => {
    setSelectedProduct(selectedProduct && selectedProduct.id === product.id ? null : product);
  };

  // Função para mostrar a lista de edição de produtos
  const showEditProductList = () => {
    setIsEditingProductListVisible(true);
    setSelectedProduct(null); // Oculta detalhes do produto selecionado
  };

  // Função para ocultar a lista de edição e voltar ao estado anterior
  const hideEditProductList = () => {
    setIsEditingProductListVisible(false);
  };

  return (
    <div>
      {/* Link para voltar para a home */}
      <button onClick={onBackToHome}>Back to Home</button>

      {/* Exibe a lista de edição de produtos se necessário */}
      {isEditingProductListVisible ? (
        <div>
          <h2>Select a product to edit</h2>
          <ul>
            {products.length > 0 ? (
              products.map((product) => (
                <li key={product.id}>
                  <button
                    type="button"
                    onClick={() => onEditProduct(product.id)} // Chama a função de editar o produto
                    style={{ textDecoration: 'underline', cursor: 'pointer', background: 'none', border: 'none', color: 'blue' }}
                  >
                    {product.name}
                  </button>
                </li>
              ))
            ) : (
              <p>No products available.</p>
            )}
          </ul>
          <button onClick={hideEditProductList}>Back to product details</button>
        </div>
      ) : (
        <div>
          <h2>Product List</h2>
          <ul>
            {products.length > 0 ? (
              products.map((product) => (
                <li key={product.id}>
                  <button
                    type="button"
                    onClick={() => handleProductClick(product)} // Exibe detalhes ao clicar no nome do produto
                    style={{ textDecoration: 'underline', cursor: 'pointer', background: 'none', border: 'none', color: 'blue' }}
                  >
                    {product.name}
                  </button>
                  {/* Exibe mais detalhes do produto ao clicar */}
                  {selectedProduct && selectedProduct.id === product.id && (
                    <div>
                      <p><strong>Name:</strong> {product.name}</p>
                      <p><strong>Price:</strong> {product.price.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}</p>
                      <p><strong>Stock Quantity:</strong> {product.stock_quantity}</p>
                      <p><strong>Description:</strong> {product.description}</p>
                      {/* Link de edição */}
                      <button onClick={showEditProductList}>Edit Product</button> {/* Exibe lista de produtos para editar */}
                    </div>
                  )}
                </li>
              ))
            ) : (
              <p>No products available.</p>
            )}
          </ul>
        </div>
      )}
    </div>
  );
};

export default ProductList;
