// src/components/ProductList.js
import React, { useState, useEffect } from 'react';
import { getProducts } from '../api';  // Verifique se o caminho está correto para o arquivo api.js

function ProductList() {
  const [products, setProducts] = useState([]);  // Estado para armazenar os produtos
  const [error, setError] = useState(null);       // Estado para armazenar erros, se houver
  const [selectedProduct, setSelectedProduct] = useState(null); // Estado para controlar o produto selecionado

  // UseEffect para carregar os produtos quando o componente for montado
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        console.log("Fetching products...");
        const response = await getProducts();  // Chama a função de API para buscar os produtos
        setProducts(response);  // Atualiza o estado com os produtos recebidos
      } catch (error) {
        setError('Error fetching products');
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);  // O array vazio [] garante que a requisição será feita apenas uma vez

  // Função para lidar com o clique em um nome de produto
  const handleProductClick = (product) => {
    setSelectedProduct(selectedProduct && selectedProduct.id === product.id ? null : product);
  };

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <div>
      <h2>Product List</h2>
      <ul>
        {products.length > 0 ? (
          products.map((product) => (
            <li key={product.id}>
              {/* Nome do produto como link */}
              <button
                type="button"
                onClick={() => handleProductClick(product)}
                style={{ textDecoration: 'underline', cursor: 'pointer', background: 'none', border: 'none', color: 'blue' }}
              >
                {product.name}
              </button>

              {/* Exibe a descrição apenas se o produto estiver selecionado */}
              {selectedProduct && selectedProduct.id === product.id && (
                <div>
                  <p><strong>Name:</strong> {product.name}</p>
                  <p><strong>Price:</strong> {product.price.toLocaleString('pt-BR', { style: 'currency', currency: 'BRL' })}</p>
                  <p><strong>Stock Quantity:</strong> {product.stock_quantity}</p>
                  <p><strong>Description:</strong> {product.description}</p>
                </div>
              )}
            </li>
          ))
        ) : (
          <p>No products available.</p>
        )}
      </ul>
    </div>
  );
}

export default ProductList;
