import React, { useState } from 'react';
import ProductForm from './ProductForm'; // Importando o formulário de produto

const ParentComponent = ({ onProductCreated }) => {
  const [isFormVisible, setIsFormVisible] = useState(false); // Controla a visibilidade do formulário

  const handleProductSaved = () => {
    console.log('Product saved!');
    setIsFormVisible(false); // Esconde o formulário após salvar
    onProductCreated(); // Chama a função para atualizar a lista de produtos
  };

  const handleCancel = () => {
    console.log('Form canceled');
    setIsFormVisible(false); // Esconde o formulário quando cancelar
  };

  return (
    <div>
      {/* Botão para exibir o formulário */}
      {!isFormVisible && (
        <button
          type="button" // Tipo button para evitar navegação
          onClick={() => setIsFormVisible(true)} // Exibe o formulário de criação de produto
        >
          Create a new product
        </button>
      )}

      {/* Exibe o formulário de criação de produto */}
      {isFormVisible && (
        <ProductForm
          onProductSaved={handleProductSaved}
          onCancel={handleCancel}
        />
      )}
    </div>
  );
};

export default ParentComponent;
