import React, { useState } from 'react';
import ProductForm from './ProductForm';

const ParentComponent = () => {
  const [isFormVisible, setIsFormVisible] = useState(false); // Estado para controlar a visibilidade do formulário

  const handleProductSaved = () => {
    console.log('Product saved!');
    setIsFormVisible(false); // Fechar o formulário após salvar
  };

  const handleCancel = () => {
    console.log('Form canceled');
    setIsFormVisible(false); // Fechar o formulário quando cancelar
  };

  return (
    <div>
      {/* Link para mostrar o formulário */}
      {!isFormVisible && (
        <a
          href="#"
          onClick={(e) => {
            e.preventDefault(); // Evita a navegação
            setIsFormVisible(true); // Exibe o formulário
          }}
        >
          Create a new product
        </a>
      )}

      {/* Exibe o formulário se isFormVisible for verdadeiro */}
      {isFormVisible && (
        <ProductForm
          onProductSaved={handleProductSaved}
          onCancel={handleCancel}
        />
      )}
    </div>
  );
};

export default ParentComponent;
