import React, { useState, useEffect } from 'react';
import { createProduct, updateProduct } from '../api'; // Importando as funções

function ProductForm({ productId, initialProduct, onProductSaved, onCancel }) {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [stock_quantity, setStockQuantity] = useState('');
  const [description, setDescription] = useState('');

  // Quando for editado, preenche os campos com os dados iniciais
  useEffect(() => {
    if (initialProduct) {
      setName(initialProduct.name);
      setPrice(initialProduct.price);
      setStockQuantity(initialProduct.stock_quantity);
      setDescription(initialProduct.description);
    }
  }, [initialProduct]);

  // Função para manipular a criação ou atualização do produto
  const handleSubmit = async (e) => {
    e.preventDefault();

    const productData = {
      name,
      price,
      stock_quantity,
      description
    };

    try {
      if (productId) {
        // Atualiza o produto existente
        await updateProduct(productId, productData);
      } else {
        // Cria um novo produto
        await createProduct(productData);
      }

      onProductSaved(); // Chama a função do componente pai para atualizar a lista de produtos
    } catch (error) {
      console.error("Error saving product:", error);
    }
  };

  return (
    <div>
      <h2>{productId ? 'Edit Product' : 'Create Product'}</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Name:</label>
          <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
        </div>
        <div>
          <label>Price:</label>
          <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} />
        </div>
        <div>
          <label>Stock Quantity:</label>
          <input type="number" value={stock_quantity} onChange={(e) => setStockQuantity(e.target.value)} />
        </div>
        <div>
          <label>Description:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
        </div>
        <div>
          <button type="submit">Save</button>
          <button type="button" onClick={onCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
}

export default ProductForm;
