import React from 'react';

const ProductList = ({ products, onProductClick, onBackToHome }) => {
  return (
    <div>
      <h2>Product List</h2>
      <ul>
        {products.length > 0 ? (
          products.map((product) => (
            <li key={product.id}>
              <button
                type="button"
                onClick={() => onProductClick(product)} // Exibe as informações do produto
                style={{ textDecoration: 'underline', cursor: 'pointer', background: 'none', border: 'none', color: 'blue' }}
              >
                {product.name}
              </button>
            </li>
          ))
        ) : (
          <p>No products available.</p>
        )}
      </ul>

      {/* Botão para voltar à tela inicial com os botões Create e List */}
      <button onClick={onBackToHome}>Voltar</button>
    </div>
  );
};

export default ProductList;
