import React, { useState, useEffect } from 'react';
import ProductList from './components/ProductList';
import ProductForm from './components/ProductForm';
import { getProducts, createProduct, updateProduct } from './api'; // Importando as funções necessárias

function App() {
  const [products, setProducts] = useState([]); // Estado para armazenar os produtos
  const [isFormVisible, setIsFormVisible] = useState(false); // Controla a visibilidade do formulário
  const [isEditVisible, setIsEditVisible] = useState(false); // Controla a visibilidade do formulário de edição
  const [productToEdit, setProductToEdit] = useState(null); // Armazena o produto a ser editado
  const [isListVisible, setIsListVisible] = useState(false); // Controla a visibilidade da lista de produtos

  // Função para buscar os produtos da API
  const fetchProducts = async () => {
    try {
      const response = await getProducts(); // Chama a API para pegar a lista de produtos
      setProducts(response); // Atualiza o estado com a lista de produtos
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  // Função chamada quando um produto é salvo ou editado
  const handleProductSaved = async (productData, productId) => {
    try {
      if (productId) {
        // Se o ID do produto estiver presente, chama a função de atualização
        await updateProduct(productId, productData);
      } else {
        // Se não houver ID, cria um novo produto
        await createProduct(productData);
      }
      fetchProducts(); // Recarrega a lista de produtos
      setIsFormVisible(false); // Fecha o formulário após salvar
      setIsEditVisible(false); // Fecha o formulário de edição
    } catch (error) {
      console.error('Error saving product:', error);
    }
  };

  // Função para iniciar a edição de um produto
  const handleEditProduct = (productId) => {
    const product = products.find(p => p.id === productId); // Encontra o produto que será editado
    setProductToEdit(product); // Armazena o produto para edição
    setIsEditVisible(true); // Exibe o formulário de edição
    setIsFormVisible(false); // Esconde o formulário de criação
  };

  // Função para mostrar a lista de produtos
  const showProductList = () => {
    setIsListVisible(true); // Exibe a lista de produtos
    setIsFormVisible(false); // Esconde o formulário de criação
    setIsEditVisible(false); // Esconde o formulário de edição
  };

  // Função para esconder a lista de produtos
  const hideProductList = () => {
    setIsListVisible(false); // Esconde a lista de produtos
  };

  useEffect(() => {
    fetchProducts(); // Carrega os produtos ao montar o componente
  }, []); // O array vazio [] garante que a requisição será feita apenas uma vez

  return (
    <div>
      <h1>Omens Product Management</h1>

      {/* Botões de navegação */}
      {!isFormVisible && !isEditVisible && !isListVisible && (
        <div>
          <button onClick={() => setIsFormVisible(true)}>Create a new product</button>
          <button onClick={showProductList}>List of Products</button>
          <button onClick={() => setIsEditVisible(true)}>Edit a product</button>
        </div>
      )}

      {/* Exibe o formulário de criação de produto */}
      {isFormVisible && (
        <ProductForm
          onProductSaved={handleProductSaved} // Passa a função de salvar para o formulário
          onCancel={() => setIsFormVisible(false)} // Função de cancelar
        />
      )}

      {/* Exibe o formulário de edição de produto */}
      {isEditVisible && productToEdit && (
        <ProductForm
          productId={productToEdit.id} // Passa o ID do produto para edição
          initialProduct={productToEdit} // Passa os dados do produto para o formulário de edição
          onProductSaved={handleProductSaved} // Função para salvar as alterações
          onCancel={() => setIsEditVisible(false)} // Função de cancelar
        />
      )}

      {/* Exibe a lista de produtos se 'isListVisible' for verdadeiro */}
      {isListVisible && (
        <div>
          <h2>List of Products</h2>
          <ProductList
            products={products} // Passa os produtos para o componente de lista
            onEditProduct={handleEditProduct} // Função para editar produtos
            onBackToHome={hideProductList} // Função para esconder a lista e voltar ao início
          />
        </div>
      )}
    </div>
  );
}

export default App;
