// src/components/ProductForm.js
import React, { useState, useEffect } from 'react';
import { createProduct, updateProduct } from '../api';

function ProductForm({ productId, initialProduct, onProductSaved, onCancel }) {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [stockQuantity, setStockQuantity] = useState('');
  const [description, setDescription] = useState('');

  useEffect(() => {
    if (initialProduct) {
      setName(initialProduct.name);
      setPrice(initialProduct.price);
      setStockQuantity(initialProduct.stock_quantity);
      setDescription(initialProduct.description);
    }
  }, [initialProduct]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const productData = {
      name,
      price,
      stock_quantity: stockQuantity,
      description,
    };

    try {
      if (productId) {
        // Atualiza um produto existente
        await updateProduct(productId, productData);
      } else {
        // Cria um novo produto
        await createProduct(productData);
      }
      onProductSaved(); // Notifica o componente pai para atualizar a lista
    } catch (error) {
      console.error('Error saving product:', error);
    }
  };

  return (
    <div>
      <h2>{productId ? 'Edit Product' : 'Create Product'}</h2>
      <form onSubmit={handleSubmit}>
        <label>Name:</label>
        <input
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
        <label>Price:</label>
        <input
          type="number"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
        <label>Stock Quantity:</label>
        <input
          type="number"
          value={stockQuantity}
          onChange={(e) => setStockQuantity(e.target.value)}
        />
        <label>Description:</label>
        <textarea
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <div>
          <button type="submit">{productId ? 'Update' : 'Create'}</button>
          <button type="button" onClick={onCancel}>Cancel</button>
        </div>
      </form>
    </div>
  );
}

export default ProductForm;
