// src/App.js
import React, { useState, useEffect } from 'react';
import ProductList from './components/ProductList';
import ProductForm from './components/ProductForm';
import { getProducts } from './api';  // Usando o getProducts corretamente

function App() {
  const [products, setProducts] = useState([]);
  const [isFormVisible, setIsFormVisible] = useState(false);
  const [isEditVisible, setIsEditVisible] = useState(false);
  const [productToEdit, setProductToEdit] = useState(null);

  const fetchProducts = async () => {
    try {
      const response = await getProducts(); // Busca os produtos da API
      setProducts(response);
    } catch (error) {
      console.error('Error fetching products:', error);
    }
  };

  const handleProductCreated = () => {
    fetchProducts(); // Recarrega os produtos após criar um novo
    setIsFormVisible(false); // Esconde o formulário após a criação
  };

  const handleEditProduct = (product) => {
    setProductToEdit(product); // Define o produto para edição
    setIsEditVisible(true); // Exibe o formulário de edição
    setIsFormVisible(false); // Esconde o formulário de criação
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <div>
      <h1>Omens</h1>

      {/* Exibir botão de criar produto */}
      {!isFormVisible && !isEditVisible && (
        <div>
          <button onClick={() => setIsFormVisible(true)}>Create a new product</button>
          <button onClick={() => setIsEditVisible(false)}>Back to Home</button>
        </div>
      )}

      {/* Formulário de criação */}
      {isFormVisible && (
        <ProductForm
          onProductSaved={handleProductCreated}
          onCancel={() => setIsFormVisible(false)}
        />
      )}

      {/* Formulário de edição de produto */}
      {isEditVisible && productToEdit && (
        <ProductForm
          productId={productToEdit.id}
          initialProduct={productToEdit}
          onProductSaved={handleProductCreated}
          onCancel={() => setIsEditVisible(false)}
        />
      )}

      {/* Lista de produtos */}
      <ProductList products={products} onEditProduct={handleEditProduct} />
    </div>
  );
}

export default App;
